const NAME = "alertDialog";

function setAlertDialog(props: CreateDialogProps) {
	const alertDialog = createDialog({ ...props });
	setContext(NAME, alertDialog);
	const {
		elements: { trigger }
	} = alertDialog;

	return trigger;
}

function getAlertDialog() {
	return getContext<Dialog>(NAME);
}

